message_list.campus_id=#{campusId,jdbcType=INTEGER}
and ( nickname like #{search,jdbcType=VARCHAR} or usersinfo.phone like #{search,jdbcType=VARCHAR} or user_asset.hire_phone like #{search,jdbcType=VARCHAR} or user_asset.charter like #{search,jdbcType=VARCHAR})
